package de.telran.chinamarket.service.impl;

import de.telran.chinamarket.entity.Photo;
import de.telran.chinamarket.entity.Product;
import de.telran.chinamarket.repository.PhotoRepository;
import de.telran.chinamarket.repository.ProductRepository;
import org.junit.jupiter.api.Test;
import org.junit.jupiter.api.extension.ExtendWith;
import org.mockito.InjectMocks;
import org.mockito.Mock;
import org.mockito.junit.jupiter.MockitoExtension;

import java.util.Optional;

import static org.junit.jupiter.api.Assertions.*;
import static org.mockito.Mockito.*;

@ExtendWith(MockitoExtension.class)
class PhotoSeviceImplTest {
    @Mock
    private PhotoRepository photoRepository;
    @Mock
    private ProductRepository productRepository;
    @InjectMocks
    private PhotoSeviceImpl photoSevice;
    @Test
    public void savePhoto_dogToys_saccess() {
        //задание начальных значений
        Product dogToys = new Product();
        Integer dogToysId = 7;
        dogToys.setId(dogToysId);
        String url = "https://img.kwcdn.com/product/open/2023-11-02/1698891848226-42cc5496700c4a19b9da10df37b97e10-goods.jpeg?imageView2/2/w/800/q/70";

        when(productRepository.findById(7)).thenReturn(Optional.of(dogToys));

        //запустить сам метод
        Photo photo2 =  photoSevice.savePhoto(dogToysId, url);

        //проверка результата
        assertEquals(photo2.getProduct().getId(), dogToysId);
        assertEquals(photo2.getUrl(), url);
        assertEquals(photo2.getBase64Image(), "");
        verify(photoRepository, times(1)).save(photo2);
    }
    @Test
    public void deletePhotoById_success() {
        Photo photoUmbrella = new Photo();
        photoUmbrella.setId(2L);

        when(photoRepository.findById(2)).thenReturn(Optional.of(photoUmbrella));

        photoSevice.deletePhotoById(2);

        verify(photoRepository, times(1)).delete(photoUmbrella);
    }
}